[code]import java.util.LinkedList;
import java.util.List;
import java.util.Random;
import java.util.concurrent.Executors;
import java.util.concurrent.ThreadPoolExecutor;

/**
 * Created by IntelliJ IDEA.
 *
 * @author Anthony Snavely
 */
public class ThreadBenchmark {

    private static final int TASK_COUNT = 10000;
    private static final int NEW_OBJECT_COUNT = 500;
    private static final int TEST_COUNT = 100;
    private static final int MAX_THREADS = 10;

    public ThreadBenchmark() {
        List<Long>[] results = new LinkedList[MAX_THREADS];
        for (int threadCount = 0; threadCount < MAX_THREADS; threadCount++) {
            System.out.println("Starting to test " + (threadCount + 1) + " threads");
            for (int l = 0; l < TEST_COUNT; l++) {
                ThreadPoolExecutor pool = (ThreadPoolExecutor) Executors.newFixedThreadPool(threadCount + 1);
                long startTime = System.currentTimeMillis();
                for (int i = 0; i < TASK_COUNT; i++) {
                    pool.submit(new Runnable() {

                        public void run() {
                            for (int i = 0; i < NEW_OBJECT_COUNT; i++) {
                                Object object = new Object();
                            }
                        }
                    });
                }
                while (pool.getCompletedTaskCount() != TASK_COUNT) ;
                long end = System.currentTimeMillis() - startTime;
                if (results[threadCount] != null) {
                    results[threadCount].add(end);
                } else {
                    List<Long> list = new LinkedList<Long>();
                    list.add(end);
                    results[threadCount] = list;
                }
                pool.shutdown();
                System.gc();
            }
        }
        for (int i = 0; i < MAX_THREADS; i++) {
            if (results[i] == null)
                continue;
            System.out.println("Completed " + TASK_COUNT + " tasks in an average " + average(results[i]) + "ms on " + (i + 1) + " threads with " + TEST_COUNT + " tests each.");
        }
    }

    public static void main(String[] args) {
        new ThreadBenchmark();
    }

    private long average(List<Long> p) {
        long sum = 0;
        for (long l : p) {
            sum += l;
        }
        return sum / p.size();
    }
}[/code]

