package org.arsenal.tools.manager;

import javax.swing.JFrame;
import javax.swing.JDesktopPane;
import javax.swing.JOptionPane;
import javax.swing.UIManager;
import javax.swing.UnsupportedLookAndFeelException;

import java.awt.SystemColor;
import javax.swing.JButton;
import java.awt.event.ActionListener;
import java.awt.event.ActionEvent;
import java.io.BufferedInputStream;
import java.io.BufferedOutputStream;
import java.io.ByteArrayOutputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.UnsupportedEncodingException;
import java.net.HttpURLConnection;
import java.net.URL;
import java.net.URLDecoder;
import java.util.regex.Pattern;

import javax.swing.JTextField;
import javax.swing.JLabel;
import javax.swing.JProgressBar;

/**
 * Created under the @see Arsenal Software Licensing
 * Downloads Youtube Videos into .mp4 formatting
 * @author Arsenal Development Team
 *
 */
public class YoutubeDownloaderApp extends JFrame {

	private JTextField videoId;
	private JTextField convertField;
	private JTextField genVideoId;
	private JLabel videoTitle;

	private JProgressBar progressBar;

	private String videoLink = "";

	public YoutubeDownloaderApp() {
		try {
			UIManager.setLookAndFeel(UIManager.getSystemLookAndFeelClassName());
		} catch (ClassNotFoundException e) {
			e.printStackTrace();
		} catch (InstantiationException e) {
			e.printStackTrace();
		} catch (IllegalAccessException e) {
			e.printStackTrace();
		} catch (UnsupportedLookAndFeelException e) {
			e.printStackTrace();
		}
		setTitle("Youtube Downloader");
		setSize(434, 413);
		setResizable(false);
		getContentPane().setLayout(null);

		JDesktopPane desktopPane = new JDesktopPane();
		desktopPane.setBackground(SystemColor.activeCaption);
		desktopPane.setBounds(0, 0, 434, 413);
		getContentPane().add(desktopPane);

		JButton btnNewButton = new JButton("Download");
		btnNewButton.addActionListener(new ActionListener() {
			public void actionPerformed(ActionEvent arg0) {
				if(videoId.getText().isEmpty() || videoId.getText().equals("")) {
					JFrame error = new JFrame();
					JOptionPane.showMessageDialog(error, "Invalid URL to Convert.", "Error", JOptionPane.ERROR_MESSAGE, null);
					return;
				}
				videoTitle.setText(getVideoTitle());
				downloadVideo(getAbstratURL(), getVideoTitle());
			}
		});
		btnNewButton.setBounds(10, 329, 414, 49);
		desktopPane.add(btnNewButton);

		videoId = new JTextField();
		videoId.setBounds(10, 35, 414, 20);
		desktopPane.add(videoId);
		videoId.setColumns(10);

		JLabel lblYoutubeVideoId = new JLabel("Youtube Video Id:");
		lblYoutubeVideoId.setBounds(10, 21, 242, 14);
		desktopPane.add(lblYoutubeVideoId);

		convertField = new JTextField();
		convertField.setBounds(10, 84, 317, 20);
		desktopPane.add(convertField);
		convertField.setColumns(10);

		JLabel lblConvertLinkTo = new JLabel("Convert Link to Video Id:");
		lblConvertLinkTo.setBounds(10, 71, 130, 14);
		desktopPane.add(lblConvertLinkTo);

		JButton convertButton = new JButton("Convert");
		convertButton.addActionListener(new ActionListener() {
			public void actionPerformed(ActionEvent e) {
				if(convertField.getText().isEmpty() || convertField.getText().equals("") || !convertField.getText().contains("http://www.youtube.com/watch?v=")) {
					JFrame error = new JFrame();
					JOptionPane.showMessageDialog(error, "Invalid URL to Convert.", "Error", JOptionPane.ERROR_MESSAGE, null);
					return;
				}
				videoLink = convertField.getText().replace("http://www.youtube.com/watch?v=", "").substring(0, 11);
				genVideoId.setText(videoLink);
			}
		});
		convertButton.setBounds(335, 83, 89, 23);
		desktopPane.add(convertButton);

		genVideoId = new JTextField();
		genVideoId.setBounds(10, 130, 414, 20);
		desktopPane.add(genVideoId);
		genVideoId.setColumns(10);

		JLabel lblGeneratedVideoId = new JLabel("Generated Video Id:");
		lblGeneratedVideoId.setBounds(10, 115, 130, 14);
		desktopPane.add(lblGeneratedVideoId);

		videoTitle = new JLabel("");
		videoTitle.setBounds(10, 304, 414, 14);
		desktopPane.add(videoTitle);

		progressBar = new JProgressBar();
		progressBar.setStringPainted(true);
		progressBar.setBounds(10, 196, 414, 35);
		desktopPane.add(progressBar);
	}

	public static void main(String[] args) {
		new YoutubeDownloaderApp().setVisible(true);
	}

	private String initialPage = "http://www.youtube.com/watch?v=";

	public String getVideoTitle() {
		String url = initialPage + videoId.getText();
		String pageSource = getContent(url, "GET");
		String title = getBetween(pageSource, "'VIDEO_TITLE': '", "',");
		if(title == null)
			title = getBetween(pageSource, "name=\"title\" content=\"", "\"");
		if(title == null)
			try {
				title = URLDecoder.decode(getBetween(pageSource, "&title=", "&"), "UTF-8").replace("+", " ");
			} catch (UnsupportedEncodingException e) {
				e.printStackTrace();
			} 
		return title;
	}

	/**
	 * Gets the Cached Url path that Youtube tries blocking the fuck out of. The encrypted page was fun to deal with
	 * @return Cached Video Url
	 */
	public String getAbstratURL() {
		String url = initialPage + videoId.getText();
		String pageSource = getContent(url, "GET");
		String title = getBetween(pageSource, "'VIDEO_TITLE': '", "',");
		if(title == null)
			title = getBetween(pageSource, "name=\"title\" content=\"", "\"");
		if(title == null)
			try {
				title = URLDecoder.decode(getBetween(pageSource, "&title=", "&"), "UTF-8").replace("+", " ");
			} catch (UnsupportedEncodingException e) {
				e.printStackTrace();
			} 
		System.out.println("Title: "+title);
		String formatUrlMap = "";
		try {
			formatUrlMap = URLDecoder.decode(getBetween(pageSource, "fmt_stream_map=", "&"), "UTF-8");
		} catch (UnsupportedEncodingException e) {
			e.printStackTrace();
		}
		if(formatUrlMap == null) 
			formatUrlMap = getBetween(pageSource, "fmt_stream_map\": \"", "\"").replace("\\/", "/");
		System.out.println("[Formatted] Url Map: "+formatUrlMap);
		String[] formatArray = formatUrlMap.split("url=");
		String mapUrl = "";
		for(int i = 0; i < formatArray.length; i++) {
			String[] parts = formatArray[i].split(Pattern.quote("&qual"));

			try {
				mapUrl = URLDecoder.decode(parts[0], "UTF-8");
			} catch (UnsupportedEncodingException e) {
				e.printStackTrace();
			}
			if(mapUrl == null || mapUrl.isEmpty() || mapUrl.equals("")) {
				continue;
			} else {
				i = formatArray.length;
				return mapUrl;
			}
		}
		return null;
	}

	/**
	 * Gets Data Values needed for download
	 * @param a 
	 * @param b
	 * @param c
	 * @return Data Stream
	 */
	public String getBetween(String a, String b, String c) {
		try {
			String[] args1 = a.split(b);
			try {
				String[] args2 = args1[1].split(c);
				if((args2[0] != null) || (args2[0] != ""))
					return args2[0];
			} catch (Exception e) {
				return args1[1];
			}
			return args1[1];
		} catch (Exception e) {

		}
		return null;
	}

	/**
	 * Gets Page Source Data
	 * @param urlPage Website Url
	 * @param httpMethod Type you are Requesting
	 * @return Content Data
	 */
	public String getContent(String urlPage, String httpMethod) {
		try {
			URL webPage = new URL(urlPage);
			HttpURLConnection connection = (HttpURLConnection) webPage.openConnection();
			connection.setRequestMethod(httpMethod);
			InputStream input = connection.getInputStream();
			ByteArrayOutputStream output = new ByteArrayOutputStream();
			byte[] buffer = new byte[1024];
			for(int bytesRead = 0; (bytesRead = input.read(buffer)) != -1;) {
				output.write(buffer, 0, bytesRead);
			}
			return output.toString();
		} catch(Exception e) {
			e.printStackTrace();
		}
		return null;
	}

	/**
	 * Downloads Youtube Video
	 * @param webPageUrl Video Download Link
	 * @param title Title of Video and File name
	 */
	public void downloadVideo(String webPageUrl, String title) {
		int bytesRead = 0;
		try {
			URL webPage = new URL(webPageUrl);
			BufferedInputStream input = new BufferedInputStream(new URL(webPage.toString()).openStream());
			BufferedOutputStream output = new BufferedOutputStream(new FileOutputStream("data/youtube/"+title+".mp4"));
			while((bytesRead = input.read()) != -1) {
				output.write(bytesRead);
				bytesRead++;
			}
			input.close();
			output.close();
			System.out.println("------------- DOWNLOAD COMPLETE -------------------");
			progressBar.setValue(100);
		} catch (IOException e) {
			e.printStackTrace();
		}
	}
}
