program killfuzzies;

var
x,y,z:integer;

procedure KillFuzzles;
begin
repeat
x:=30;y:=50;z:=1;
repeat
Repeat
      clickmouse(x,y,true);
      wait(1);
      x:=x+10+random(5);
Until(x>450)
  if(getcolor(264, 107)=65280)then
  begin
    movemouse(300, 231);
    wait(50);
    clickmouse(300, 231,true);
    wait(500);
    movemouse(172, 217);
    wait(50);
    clickmouse(172, 217,true);
    wait(500);
    z:=30;
    end;

  if(getcolor(210, 151)=65280)then
  begin

    movemouse(118, 193);
    wait(50);
    clickmouse(118, 123,true);
    wait(50);

    movemouse(124, 123);
    wait(50);
    clickmouse(124, 193,true);
    wait(50);

    movemouse(394, 271);
    wait(50);
    clickmouse(394, 271,true);
    wait(50);
    end;

y:=y+125;
x:=1;

Until(y>375);
y:=175;
until(z=30);
end;


begin
  KillFuzzles;
end.[/code][br][br][size=1]Posted on: January 01, 2008, 03:08:14 PM[/size][hr]heh I have a new version that actually targets/hits bonus' when they get to x3.....but that one beats the current high score every time....so I'm not posting just yet......still has a few minor bugs too
