function IsWelcomeScreen: Boolean;
begin
  if(GetColor(271, 292) = 8356779)and
    (GetColor(499, 292) = 8356779)and
    (GetColor(271, 381) = 8356779)then
    Result := True;
end;

procedure WelcomeScreen;
begin
  if(not(IsWelcomeScreen))then Exit;
  MouseBox(275, 296, 495, 377, 1);
end;
//-----------------------------------------------//

Procedure Setup;
begin
  SetupSRL;
  ActivateClient;
  ChatsOff;
  If (LoggedIn) then
  begin
  HighestAngle;
  MakeCompass('n');
  end;
  If (not(LoggedIn)) then
  LoginPlayer;
  WelcomeScreen;
  HighestAngle;
  MakeCompass('n');
end;
//-----------------------------------------------//

Procedure ProgReport;
begin
  ClearReport;
  Writeln('===============================')
  Writeln('=        You Are Using        =');
  Writeln('=                             =');
  Writeln('=  The Skys Shadow Edgeville  =');
  Writeln('=                             =');
  Writeln('=         YEW CUTTER!         =');
  Writeln('=                             =');
  WriteLn('=        Version ' + VersionNumber + '         =');
  Writeln('=                             =');
  Writeln('=        ' + inttostr(Trips) + ' Trips done         =');
  Writeln('=                             =');
  Writeln('=        Happy Cutting        =');
  Writeln('===============================');
end;
//-----------------------------------------------//

Procedure WalktoTrees;
begin
  If (FindSymbol(x,y, 'rare trees')) then
  begin
   SetRun(true)
   Mouse(x,y,1,1,true)
   wait(10000)
  end
  If (not(FindSymbol(x,y, 'rare trees')))then
  begin
   SetRun(true)
   Mouse(747,280,1,1,true)
   wait(5000)
    If (FindSymbol(x,y, 'rare trees')) then
     begin
      Mouse(x,y,1,1,true)
      wait(10000)
     end
  end
end;

//-----------------------------------------------//

Procedure ChoptheTrees;
begin
  repeat
    if (FindMSColorTol(x,y,3438704,50))then
     MMouse(x,y,1,1)
      If IsUpText('Chop') then
      Mouse(x,y,1,1,true)
      wait(5000+Random(5000)+Random(5000)+Random(5000))
  until (invfull = true)
    if (invfull = true) then
    Trips := Trips + 1;
end;
//-----------------------------------------------//

Procedure WalktoBank;
begin
  If (FindSymbol(x,y,'bank'))then
   begin
    MMouse(x,y,1,1)
    Mouse(x,y,1,1,true)
   end
  If (not(FindSymbol(x,y,'bank')))then
   begin
   Mouse(789,173,1,1,true)
   wait(10000);
    If (FindSymbol(x,y,'bank'))then
     begin
      MMouse(x,y,1,1)
      Mouse(x,y,1,1,true)
     end
   end
end;
//-----------------------------------------------//

Procedure BanktheLogs;
begin
  OpenBank;
  If WieldAxe = 1 then
    begin
      DepositAll;
    end
  If WieldAxe = 2 then
    begin
      Deposit(2,28,2);
    end
end;
//-----------------------------------------------//

Procedure AntiRandomsandAntiBan;
begin
  SRLRandomsReport;
  FindNormalRandoms;
  FindAxeHeadColor;
  AttachHead;
  DontBanMe(2);
  If (FindEnt (3438704)) then
   begin
    Logout;
    Wait(30000)
    LoginPlayer;
   end
end;
//-----------------------------------------------//

begin
 Setup;
  repeat
   ProgReport;
   WalktoTrees;
   ChoptheTrees;
   WalktoBank;
   BanktheLogs;
   AntiRandomsandAntiBan;
  until (false)
end.
