http://en.wikipedia.org/wiki/Luhn_algorithm

A algorithm for Verifying the accuracy and validity of credit card numbers.

[code]
    function isvalid($str)
    {
        $endnumber = 0;
        $array = str_split($str);

        if($str % 1 != 0)
        {
            return false;
        }

        for($i = 0; $i <= strlen($str); $i++)
        {
            if($i == 0 || ($i % 2) == 0)    
            {
                if(($array[$i] * 2) >= 10)
                {
                    $new_numbers = str_split($array[$i] * 2);

                    $endnumber += ($new_numbers['0'] + $new_numbers['1']);
                }    else    {
                    $endnumber += ($array[$i] * 2);
                }
            }
            else
            {
                $endnumber += $array[$i];
            }
        }

        if($endnumber % 10 == 0)
        {
            return true;
        }    else    {
            return false;
        }
    }  
[/code]

