procedure CreateFree;
begin
  if (FindBitmap(CreateNew, x, y)) then
  begin
    ClickMouse(x, y, True);
    Wait(3000 + Random(2000));
  end;
end;

procedure AgeDropdown;
begin
  SendText(chr(9));
