procedure BackToStart;
begin
  sendKeys(Chr(9));
  Wait(300)
    SendKeys(Chr(13));
  Sleep(10000 + Random(3000)); //Waits 10 + a random 3 seconds before
  c := c + 1; //making the next account.
end;


