//======================Main Loop==================================
begin
  SetupSRL;
  LoadBitmaps;
  ClearDebug;
  Credits;
  Sleep(1000);
  ActivateClient;
  Wait(2000);
  C := 0;
  repeat
    CreateFree;
    AgeDropdown;
    DesUsername;
    TypeUserName;
    IAgree;
    MakeIt;
    BackToStart;
  until (c >= HowManyAccounts)
end.
