Procedure WriteWordInt(a: Integer);
 begin
  If a > 255 then
    begin
    b := (a and $00ff);
    c := a and $ff00;
    d := c shr 8;
    //   WriteLn(Output, b);
    //   WriteLn(Output, c);
    //   WriteLn(Output, d);
    BlockWrite(Fout, d, 1);
    BlockWrite(Fout, b, 1);
    end
  else
    begin
      b := 0;
      BlockWrite(Fout, b, 1);
      BlockWrite(Fout, a, 1);

    end;
 end;

 Procedure WriteInt(a: Integer);
 begin
  If a > 255 then
    begin
    b := (a and $00ff);
    c := a and $ff00;
    d := c shr 8;
    //   WriteLn(Output, b);
    //   WriteLn(Output, c);
    //   WriteLn(Output, d);
    BlockWrite(Fout, d, 1);
    BlockWrite(Fout, b, 1);
    Addit := 1;
    end
  else
    begin
      BlockWrite(Fout, a, 1);
    end;
 end;

 Procedure WriteSmInt(a: Integer);
 begin
      BlockWrite(Fout, a, 1);
 end;

 Procedure WriteBigInt(a: Integer);
 begin
    b := (a and $00ff);
    c := a and $ff00;
    d := c shr 8;
    //   WriteLn(Output, b);
    //   WriteLn(Output, c);
    //   WriteLn(Output, d);
    BlockWrite(Fout, d, 1);
    BlockWrite(Fout, b, 1);
 end;



 Procedure MakeHex(n1: Integer);
 begin
  if (n1 > -64) and (n1 < 63) then
    begin
      a := n1 + 64;
//      b := a and $7f;
      WriteSmInt(a);
      Addit := 0;
    end
  else
    begin
    a:= n1 + 49152;
//    b:= a or $8000;
  WriteBigInt(a);
  Addit := 1;
  end;
end;



