[code=java]private static byte[] getModel(int Index) {
		try {


			File Model = new File("C:/FSRv1/Models/"+Index+".dat");
			byte[] aByte = new byte[(int)Model.length()];
			if (aByte != null && aByte.length > 0) {
				FileInputStream Fis = new FileInputStream(Model);
				Fis.read(aByte);
				Fis.close();
				return aByte;
			} else {
				System.out.println("Unable To Find Model "+Index);
				return null;
			}
		} catch(Exception e) {
			System.out.println("Error Reading Model");
			return null;
		}
	}[/code]

