[code=java]
package thread;

import java.util.concurrent.ScheduledExecutorService;

import thread.ExecutionThreadListener.ExecutionThreadState;

public abstract class ExecutionThread {

	private ExecutionThreadState state = ExecutionThreadState.PENDING;

	public abstract void destruct();

	public abstract void execute();

	public abstract int getExecutionDelay();

	public abstract ScheduledExecutorService getExecutionService();

	public ExecutionThreadState getState() {
		return state;
	}

	public void setState(ExecutionThreadState state) {
		this.state = state;
	}
}
