program Autotalker; {.Include SRL/SRL.Scar} {.include srl\srl\extended\xtext.scar}
const //type the messages you want to say here
msg1 ='white:wave:selling coal 250ea'; msg2 ='selling coal 250ea-mjordan'; msg3 ='white:coal 250gp each!!!'; msg4 ='selling coal~mjordan~';
procedure credits; begin writeln('<========================================================>') writeLn(' Thankyou for using Mjordans') writeLn(' Simple Autotalker.') writeLn('Big thanks to Rons Autotalker Where I Got Some Ideas From,') writeLn('All Of The Tuts With Which I Used That Helped Me Lots,') writeLn('And Most Of All Thanks To Kaitnieks For Creating SCAR') writeLn('<========================================================>') end;
//procedure SendMyText(Text: String); //end;
procedure RandomTalk; begin case Random(4) of 0 : SendMyText(msg1); 1 : SendMyText(msg2);//randomly types the messages. is very human like. 2 : SendMyText(msg3); 3 : SendMyText(msg4); End; End;
begin setupsrl; credits; activateclient; wait(200); repeat RandomTalk; Wait(2000+Random(3000)); Until(IsFKeyDown(12))//if you want to end the script just press F12 End.[/scar]
