begin
loadlibrary(\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\'Internet Explorer\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\');
Run(true);
SetVBright;
SetCat(\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\'Off\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\');
