import java.awt.Color; // importation of the Color class

public class VehicleApp extends wheels.users.Frame { // creation of instance variables
	private wheels.users.RoundedRectangle _head;
	private wheels.users.Rectangle _body;
	private wheels.users.Ellipse _innerWheel1, _outerWheel1, _innerWheel2, _outerWheel2;
	private wheels.users.ConversationBubble _convo;

	public VehicleApp () { // constructor definition
		_head = new wheels.users.RoundedRectangle();
		_body = new wheels.users.Rectangle();
		_outerWheel1 = new wheels.users.Ellipse();
		_innerWheel1 = new wheels.users.Ellipse();
		_outerWheel2 = new wheels.users.Ellipse();
		_innerWheel2 = new wheels.users.Ellipse();
		_convo = new wheels.users.ConversationBubble("I have trouble dressing myself in matching truck parts.");
		changeCol(); // invocation of methods defined below
		changeSize();
		changeLoc();
	}

	public void changeCol () { // method where colors of instances are handled
		_body.setColor(Color.CYAN);
		_head.setColor(Color.RED);
		_innerWheel1.setColor(Color.GREEN);
		_outerWheel1.setColor(Color.BLACK);
		_innerWheel2.setColor(Color.YELLOW);
		_outerWheel2.setColor(Color.PINK);
		_convo.setColor(Color.LIGHT_GRAY);
	}

	public void changeSize () { // method where sizes of instances are handled
		_body.setSize(250,100);
		_head.setSize(100,100);
		_innerWheel1.setSize(30,30);
		_outerWheel1.setSize(70,70);
		_innerWheel2.setSize(30,30);
		_outerWheel2.setSize(70,70);
	}

	public void changeLoc () { // method where locations of instances are handled
		_body.setLocation(175,200);
		_head.setLocation(425,220);
		_innerWheel1.setLocation(210,320);
		_outerWheel1.setLocation(190,300);
		_innerWheel2.setLocation(350,320);
		_outerWheel2.setLocation(330,300);
		_convo.setLocation(320,80);
	}

	public static void main (String[] args) { // allows the VehicleApp class to execute
		VehicleApp app = new VehicleApp();
	}

}
[/code]

