//->GetRandomLoad-//
Var
RandomLoad : Integer;

Procedure GetRandomLoad;
Begin
RandomLoad := (AmmountOfLoads + Random (5))
end;


//->Anti Randoms<-//
Procedure TheAntiRandoms;
Begin
Status('Random Check')
FindNormalRandoms;
wait(10)
FindTalk;
wait(10)
if (FindFight) then
begin
Status('Running From a fight.')
RunAwayDirection('E');
Wait(8500 + random(3500));
RunBack;
end;
Wait(10);
FindTalk;
end;


