channel.description:
program chopatree;
{.include srl/srl.scar}
{.include srl/srl/skill/woodcutting.scar}

const
treecolor1=1138768;
timetochop=10000;

procedure chop;
begin
if(findcolorspiraltolerance(x,y,treecolor1,msx1,msy1,msx2,msy2,25))then
mmouse(x,y,3,3)
if(IsUpTextMulti('chop','op do','down'))then
ClickMouse(3, 3, True)
begin
mouse(x,y,3,3,true)
end;
end;

procedure treewait;
begin
wait(timetochop)
end;


begin
cleardebug;
setupsrl;
activateClient;
repeat
treewait;
chop;
until(false)
end.




