    public boolean ApplyDead() {
        if (IsDeadTimer == true) {
            actionAmount++;
            actionTimer = 0;
            ResetAttack();
            ResetAttackNPC();
            pEmote = 2304;
            IsDeadTimer = true;
            ApplyDead();
            specialAttacks();
            specialAttacks2();
            specialAttacks3();
            specialAttacks4();
            specialAttacks5();
            specialAttacks6();
        }
        if (actionTimer == 0 && IsDeadTimer == false) {
            keepItem1();
            keepItem2();
            keepItem3();
            deleteItem(keepItem, getItemSlot(keepItem), keepItemAmount);
            deleteItem(keepItem2, getItemSlot(keepItem2), keepItemAmount2);
