                                                                    ~Wall's Never-Log-Out V.1~
                                      -------------------------------------------------------------[/size]
*[size=3pt]I am not responsible for any Offences, use this at OWN RISK[/size]




[scar]program Online;
{.include SSI2.txt}
var
x1,y1,xx,yy: Integer;

Procedure AFK;
Begin
 GetMousePos(xx,yy)
 MMouse(xx,yy,2+Random(12),2+Random(12))
End;

Procedure CheckStatus;
Begin
 Wait(1000+Random(500))
 GetMousePos(x,y)
 Status('M1: ' + IntToStr(x) + ':' + IntToStr(y) + '')
 Wait(8000+Random(500))
 GetMousePos(x1,y1)
 Status('M2: ' + IntToStr(x1) + ':' + IntToStr(y1) + '')
 Writeln('Mouse started at ' + IntToStr(x) + ':' + IntToStr(y) + ' to ' + IntToStr(x1) + ':' + IntToStr(y1) + '')
 If x=x1 then
  Begin
   AFK;
  End;
End;

begin
 Repeat
  CheckStatus;
  Wait(2000+Random(2000))
 Until(False)
end.[/scar]
