	public final void updateHitData(int markType, int damage, int l, int icon)
	{
		for(int i1 = 0; i1 < 4; i1++)
			if(hitsLoopCycle[i1] <= l)
			{
				hitIcon[i1] = icon;
				hitmarkMove[i1] = 5;
				hitmarkTrans[i1] = 230;
			hitArray[i1] = damage;
				hitMarkTypes[i1] = markType;
				hitsLoopCycle[i1] = l + 70;
				return;
			}
	}
