channel.description:
This is a yell system I wrote that favors no one in particular, just takes 30 seconds off the timer for donators from the normal 60 second timer for normal players:

Commands.java
[code=java]if (playerCommand.startsWith("yell")) {
		if (!Connection.isMuted(c)) {
			if (playerCommand.substring(5).contains("@")) {
				c.sendMessage("You may not use the symbol '@'.");
				return;
			}
			for (String blocked: Config.Restricted_Words) {
				if (playerCommand.substring(5).contains(blocked)) {
					c.sendMessage("The yell filter has just blocked your message! Don't advertise links or sware over yell!");
					return;
				}
			}
		}
		if (Connection.isMuted(c)) {
			c.sendMessage("You're muted and you may not use yell. Please follow the rules before being muted again!");
			return;
		}
		for (int j = 0; j < PlayerHandler.players.length; j++) {
			if (PlayerHandler.players[j] != null) {
				Client c2 = (Client)PlayerHandler.players[j];
		switch (c.playerRights) {

		case 0:
			if (System.currentTimeMillis() - Player.yellDelay < 60000) {
				 c.sendMessage("It has only been @red@"+ Player.getTimeLeft(c) +"@bla@ seconds since you last yelled!");
		         c.sendMessage("Players can only yell once every minute. Donate to yell once every @red@30@bla@ seconds!");
		         return;
			}
			c2.sendMessage("[Player]" + c.playerName + ": " + Misc.optimizeText(playerCommand.substring(5))); 
			Player.yellDelay = System.currentTimeMillis();
			break;
		case 1:
			c2.sendMessage("[@whi@Moderator@bla@]" + c.playerName + ": " + Misc.optimizeText(playerCommand.substring(5)));
			break;
		case 2: 
			c2.sendMessage("[@yel@Administrator@bla@]" + c.playerName + ": " + Misc.optimizeText(playerCommand.substring(5)));
			break;
		case 3:
			if (c.playerName.equalsIgnoreCase("Riipiin")) {
				c2.sendMessage("[@dre@VR Developer@bla@]" + c.playerName + ": " + Misc.optimizeText(playerCommand.substring(5)));
			} else {
			c2.sendMessage("[@yel@Owner@bla@] <img=1>" + c.playerName + ": " + Misc.optimizeText(playerCommand.substring(5)));
			}
			break;
		case 4:
			if (System.currentTimeMillis() - Player.yellDelay < 30000) {
				 c.sendMessage("It has only been @red@"+ Player.getTimeLeft(c) +"@bla@ seconds since you last yelled!");
				 c.sendMessage("Remeber, donators can only yell every 30 seconds!");
		         return;
			}
			c2.sendMessage("[@red@Donator@bla@]"  + c.playerName + ": " + Misc.optimizeText(playerCommand.substring(5)));
			Player.yellDelay = System.currentTimeMillis();
			break;
		}
	}
		}
	}[/code]

Player.java
[code=java]public static long yellDelay;

	public static long getTimeLeft(Client c) {
		return (System.currentTimeMillis() - yellDelay) / 1000;
	}[/code]

Config.java
[code=java]public static String[] Restricted_Words = { "www.", ".com", "c0m", "fuck", "nigger", "shit", "dick", "niggar", ".net", ".org", ".webs.com", ".biz", 
		"cunt",  "prick", "gay"  };[/code]
