//guide coming soon, used in fally

program cowmanregcut;
{.include squig.txt}
//must have squig v4.4

//weild your axe
//start at trees
//set if using reg rs or if set to 000
const treecolor=2046004; //top of the tree, set yourself if using reg
const treecolort=2115648; //look above
const bankcolor=3060683; //The bank symbol on the mini map
const waitt=5000;

var
x,y: integer;

//BY COWMAN!!!


procedure randoms;
begin
FindRandoms;
end;

procedure chop;
var x,y : integer;
begin
randoms;
if(findcolorspiral(x,y,treecolor,3,3,515,336)) Then
Wait (waitt+random(500))
movemousesmooth(x,y)
wait(1000)
if(IsTextAt3(9,9,'Chop down',100))
then clickmouse(x,y,True)
end;

procedure chopt;
var x,y : integer;
begin
randoms;
if(findcolorspiral(x,y,treecolorT,3,3,515,336))Then
Wait (waitt+random(500))
movemousesmooth(x,y)
wait(1000)
if(IsTextAt3(9,9,'Chop down',100))
then clickmouse(x,y,True)
end;

procedure walktobank;
var x,y : integer;
begin
if(findcolorspiral(x,y,bankcolor,566,20,752,166))Then
Wait (waitt+random(500))
movemousesmooth(x,y)
wait(500+random(200));
clickmouse(x,y,true)
randoms;
end;

procedure dropinbank;
begin
openbank;
depositall;
closebank;
end;

procedure walktotrees;
begin
x:=608;
y:=71;
mouser(x,y,3,3,1)
randoms;
wait(4000)
end;

procedure banktrip;
begin
walktobank;
dropinbank;
walktotrees;
end;

procedure PrintProgressReport;
begin
      WriteLn('main loop completed - beta');
end;




begin
setupsquig;
