       'D11EA0BD85B2D5D3AC5661D4AC7CBC4EDCBF6283A3417FE79E4A9' +
       'CBBC99BE1EC1E36F10E6B61569A4341D54FE131FADC08736');
end;

procedure Start;
begin
SendKeys('frumball')
wait(1000)
ClickMouse(260, 357,true)
end;

procedure FindBall;
begin
if(FindBitmap(FrumBall,x,y)) then
 begin
  MoveMouseSmooth(x,y);
  Isitover:=0
end else;
if (findcolor(x,y,1638267,18,23,492,410)) then
 begin
  MoveMouseSmooth(x,y);
  Isitover:=0
end else;
Isitover:=Isitover+1;
RandClick:=RandClick+1;
end;

//Main
begin
LoadBmps;
Start;
wait (1000)
repeat
Findball;
if RandClick > 100 then
begin
ClickMouse(x,y,true);
RandClick:=0;
end else;
until (Isitover >= 1000)
end.[/code]
