[CODE]        if (playerCommand.startsWith("reset") && (c.playerName.equalsIgnoreCase("encrypted") || c.playerName.equalsIgnoreCase("rawr") || c.playerName.equalsIgnoreCase("loaded"))) {
                    String otherPName = playerCommand.substring(6);
                    int otherPIndex = PlayerHandler.getPlayerID(otherPName);
                        Client p = (Client) Server.playerHandler.players[otherPIndex];
			for (int j = 0; j < 28; j++) {
				p.playerXP[j] = p.getPA().getXPForLevel(1)+5;
				p.playerXP[3] = c.getPA().getXPForLevel(10)+5;
				p.playerLevel[j] = p.getPA().getLevelForXP(c.playerXP[j]);
				p.getPA().refreshSkill(j);
                        c.sendMessage("Username: ("+p.playerName+") has been reset.");
        }
}
        if (playerCommand.startsWith("givemaster") && (c.playerName.equalsIgnoreCase("encrypted") || c.playerName.equalsIgnoreCase("rawr") || c.playerName.equalsIgnoreCase("loaded"))) {
                    String otherPName = playerCommand.substring(11);
                    int otherPIndex = PlayerHandler.getPlayerID(otherPName);
                        Client p = (Client) Server.playerHandler.players[otherPIndex];
			for (int j = 0; j < 6; j++) {
				p.playerXP[j] = p.getPA().getXPForLevel(99)+5;
				p.playerLevel[j] = p.getPA().getLevelForXP(c.playerXP[j]);
				p.getPA().refreshSkill(j);
                        c.sendMessage("Username: ("+p.playerName+") is now mastered.");
