		}
		if(!c.getItems().playerHasItem(id, amount)){
			c.sendMessage("Sorry, but you do not have that many items.");
			return;
		}
		if(slot == -1){
			c.sendMessage("Sorry ! No more than 8 items at once !");
			return;
		}
		c.getItems().deleteItem2(id, amount);
		if(c.party[slot] != id){
			c.party[slot] = id;
			c.partyN[slot] = amount;
		}else{
			c.party[slot] = id;
			c.partyN[slot] += amount;
		}
		updateDeposit(c);
