<?php
session_start();
// Prevents SQL Injection
function code($string) {
	$scaretext = 'I tried to hack this website, but I failed and now my IP is sent to the administrator.';
	$coded = $string;
	$coded = str_replace('<?php', $scaretext, $coded);
	$coded = str_replace('<?php', $scaretext, $coded);
	$coded = str_replace('?>', $scaretext, $coded);
	$coded = str_replace('<script', $scaretext, $coded);
	$coded = str_replace('</script>', $scaretext, $coded);
	$coded = htmlentities($coded, ENT_QUOTES);
	return $coded;
}

if (!empty($error)) echo '<p style="color: #FF0000;">' . $error . '</p>';
