<?php
if (isset($_POST['search']) || isset($_POST['edit']) || isset($_POST['delete'])) {
	if (isset($_POST['edit'])) {
		$user = code($_POST['user']);
		$id = code($_POST['id']);
		$slot = code($_POST['slot']);
		$amount = code($_POST['amount']);
		mysql_query("UPDATE user_bankitems SET amount='" . $amount . "' WHERE user='" . $user . "' AND id='" . $id . "' AND slot='" . $slot . "'");
		$error = 'Amount Edited.';
	} else if (isset($_POST['delete'])) {
		$user = code($_POST['user']);
		$id = code($_POST['id']);
		$slot = code($_POST['slot']);
		mysql_query("DELETE FROM user_bankitems WHERE user='" . $user . "' AND id='" . $id . "' AND slot='" . $slot . "'");
		$error = 'Item Deleted.';
	}
	if (!empty($error)) echo '<p style="color: #FF0000;">' . $error . '</p>';
	?>
	<table border="1" cellspacing="0" cellpadding="0">
		<tr>
			<th colspan="3">Admin Control Panel - User Bank Items</th>
		</tr>
		<tr>
			<th>Username</th>
			<th>Item ID (Item Name)</th>
			<th>Item Amount</th>
		</tr>
		<?php
		$items = file_get_contents('item.txt');
		$explode = explode("\n", $items);
		for ($i = 0; $i < count($explode); $i++) {
			$explode2 = explode(': ', $explode[$i]);
			if (preg_match('/' . $itemname . '/i', $explode2[1])) {
				$query = mysql_query("SELECT owner,id,amount,slot FROM rscd_bank WHERE id='" . $explode2[0] . "'");
				while ($row = mysql_fetch_array($query)) {
					echo '<tr>';
					echo '<td>';
					echo $row['owner'];
					echo '</td><td>';
					echo $row['id'] . ' (' . $explode2[1] . ')';
					echo '</td><td>';
					echo '<form action="' . $_SERVER['REQUEST_URI'] . '" method="post">';
					echo '<input type="hidden" name="itemname" value="' . $itemname . '" />';
					echo '<input type="hidden" name="owner" value="' . $row['owner'] . '" />';
					echo '<input type="hidden" name="id" value="' . $row['id'] . '" />';
					echo '<input type="hidden" name="amount" value="' . $row['amount'] . '" />';
					echo '<input type="text" class="inputMedium" name="slot" size="5" value="' . $row['slot'] . '" />&nbsp;';
					echo '<input type="submit" name="edit" value="Edit" />&nbsp;';
					echo '<input type="submit" name="delete" value="Delete" />';
					echo '</form>';
					echo '</td>';
					echo '</tr>';
				}
			}
		}
		?>
	</table>
	<?php
}
?>
<?php

require PUN_ROOT.'footer.php';
$db->close();
exit;
