		$error = 'username unbanned.';
	} else if (isset($_POST['ban'])) {
		$username = code($_POST['username']);
		mysql_query("UPDATE rscd_players SET banned=1 WHERE username='" . $username . "'");
		$error = 'username banned.';
	}
	if (!empty($error)) echo '<p style="color: #FF0000;">' . $error . '</p>';
	?>
	<table border="1" cellspacing="0" cellpadding="0">
