      $con = mysql_connect("localhost","root","");
         if (!$con)
         {
         die('Could not connect: ' . mysql_error());
         }   
      mysql_select_db("game_db", $con);
      $result = mysql_query("SELECT * FROM game_table ORDER BY game_$Which $Order LIMIT $RecordNo, 1");
      while($row = mysql_fetch_array($result))
