battle.php
[code]
<html>
<body>
<?php

// Declaring variables

srand((double)microtime()*1000000); 
$name = $_COOKIE["name"];
$xp = $_COOKIE["xp"];
$levelat = $_COOKIE["levelat"]
$hp = 100;
$ehp = 100;
$damage=rand(0,50);
$edamage=rand (0,50);

// Attacking

?>
<?php echo $name . " strikes his enemy for " . $damage . "!"; ?> <br />
<?php $hp = $hp - $edamage; ?>
<?php echo "Enemy now has " . $ehp . " out of 100 health"; ?> <br /><?php

// Attacked

echo "Enemy hits you for " . $edamage . "!"; ?> <br />
<?php $ehp = $ehp - $damage; ?><?php
echo "You now have " . $hp " out of 100 health"; ?> <br /> <?php

// Check for winner

if($ehp<=0)
{
   if($hp>=1)
     {
        echo "You Win!";
     }
}
elseif($hp<=0)
{
   if($ehp>=1)
     {
	echo "You Lose!";
     }
}
else
{
  echo "Next Move";
  goto fight:
}
?>
</body>
