<?php

$rs = file_get_contents("http://www.runescape.com/title.ws");

$pos = strpos($rs, 'There are currently');
$number = substr($rs, $pos + 20, 50);
$pos = strpos($number, ' people playing');
$number = substr_replace($number, '', $pos);

//echo "There are $number people playing RuneScape!"; // Remove the // at the start for if you don't want an image

header ("Content-type: image/Gif"); // remove line if you don't want an image
$back = imagecreatefromgif ("players.gif"); // remove line if you don't want an image
$textcolor = ImageColorAllocate ($back, 255, 255, 255); // remove line if you don't want an image

ImageString ($back, 2, 23, 2, "There are currently $number people playing RuneScape!", $textcolor); // remove line if you don't want an image
ImageGif ($back); // remove line if you don't want an image
ImageDestroy ($back); // remove line if you don't want an image

?> 
