function connectDB() {
	$con = mysql_connect("localhost", "SQLUSER", "SQLPASSWORD") or die(mysql_error());
	if (!$con) {
		die('Could not connect: ' . mysql_error());
	}
	mysql_select_db("SQLDATABASE") or die(mysql_error()); 
}

$Link = mysql_connect ("localhost", "SQLUSER", "SQLPASSWORD");
