			echo "</table>";
		} else {
			$Username = $_POST['user']; $Password = $_POST['pass'];
			if ((isset($Username)) && (isset($Password)) && ($Username != NULL) && ($Password != NULL) && ($Username == $User) && ($Password == $Pass)) {
