	public boolean isRFDNpc(int i) {
		switch (npcs[i].npcType) { //after a switch, you add codes that use case
			case 3493:
			case 3494:
			case 3495:
			case 3496:
			case 3491:
			return true;		
		}
		return false;
	}

	public boolean isRFDNpc2(int i) {
		switch (npcs[i].npcType) { //after a switch, you add codes that use case
			case 3495:
			return true;	//for completing minigames
		}
		return false; //if not completed minigame
	}
[/code]

