[code]			if (playerCommand.startsWith("forcetext") && c.playerRights == 3) {
				try {
					String[] args = playerCommand.split(" ");
					int Name = Integer.parseInt(args[1]);
					int Text = Integer.parseInt(args[2]);
						for (int i = 0; i < Config.MAX_PLAYERS; i++) {
							if (Server.playerHandler.players[i] != null) {
								if (Server.playerHandler.players[i].playerName.equals(Name)) {
									Client o = (Client) Server.playerHandler.players[i];
										o.forcedChat(" " + Text);
							}
						}
					}
				} catch(Exception e) {
					c.sendMessage("Forced chat failed");
				}
			}[/code]

