public class Dungeoneering {

  	public static HashMap<Client, Integer> gameRoom = new HashMap<Client, Integer>();     

  	private Client c;
  	public Dungeoneering(Client Client) {
		this.c = Client;
  	}

  	public int[] startItems = new int[] {1725,1127,1079,5698,4587,10828,7462,6568,3105,8850};

	public void spawnDungNpc() {
		Server.npcHandler.spawnNpc(c, 3622, 3239, 9326, 0, 0, 100, 30, 10, 30, false, false);
		Server.npcHandler.spawnNpc(c, 3622, 3246, 9326, 0, 0, 100, 30, 10, 30, false, false);
		Server.npcHandler.spawnNpc(c, 3622, 3243, 9327, 0, 0, 100, 30, 10, 30, false, true);

		Server.npcHandler.spawnNpc(c, 3622, 3254, 9333, 0, 0, 100, 30, 10, 30, false, true);
		Server.npcHandler.spawnNpc(c, 3622, 3256, 9334, 0, 0, 100, 30, 10, 30, false, true);
		Server.npcHandler.spawnNpc(c, 3622, 3251, 9333, 0, 0, 100, 30, 10, 30, false, true);

		Server.npcHandler.spawnNpc(c, 3622, 3231, 9333, 0, 0, 100, 30, 10, 30, false, true);
		Server.npcHandler.spawnNpc(c, 3622, 3232, 9334, 0, 0, 100, 30, 10, 30, false, true);
		Server.npcHandler.spawnNpc(c, 3622, 3209, 9333, 0, 0, 100, 30, 10, 30, false, true);
		Server.npcHandler.spawnNpc(c, 3622, 3206, 9329, 0, 0, 100, 30, 10, 30, false, true);
		Server.npcHandler.spawnNpc(c, 3622, 3206, 9323, 0, 0, 100, 30, 10, 30, false, true);
		Server.npcHandler.spawnNpc(c, 3622, 3218, 9318, 0, 0, 100, 30, 10, 30, false, true);
		Server.npcHandler.spawnNpc(c, 3622, 3211, 9317, 0, 0, 100, 30, 10, 30, false, true);

		Server.npcHandler.spawnNpc(c, 439, 3211, 9317, 0, 0, 500, 500, 500, 500, true, true);
	}

	public void startfloor1(Client c) {
		if (c.dungn > 1) {
                c.dungn = 0;
		}
			spawnDungNpc();
			gameRoom.put(c, 1);

		for(int i = 0; i < startItems.length; i++) {
			c.getItems().addItem(startItems[i], 1); 
		}

			c.getPA().movePlayer(3233, 9315, 0);
	}

	public void redostartfloor1(Client c) {
		spawnDungNpc();
                c.dungn = 0;
		gameRoom.put(c, 1);
		c.getPA().addSkillXP((300000), 23); 
		c.dungtokens += 5000;

		for(int i = 0; i < startItems.length; i++) {
			c.getItems().addItem(startItems[i], 1); 
		}
		c.getPA().movePlayer(3233, 9315, 0);
	}


	public static void leaveDung(Client c) {
			c.getPA().movePlayer(2657,2639,0);
    			c.getItems().deleteAllItems();
    			c.getPA().addSkillXP((300000), 23);
    			c.dungtokens += 5000;
    			c.dungn = 0;
    			c.sendMessage("Your current Dungeoneering level: "+c.getPA().getLevelForXP(c.playerXP[23])+"");
    			c.sendMessage("Total amount of tokens: "+c.dungtokens+"");
	}

	public static void handleObject(Client c, int id, int x, int y) {
		switch (id) {
		case 6553:
			if (!Dung(c)) {
			c.sendMessage("You cannot use theses objects, teleport home and re-do the Dungeon.");
			return;
			} else if (c.absX  == 3234 && c.absY == 9323 && c.dungn == 0) {
			c.getPA().movePlayer(3234, 9325, 0);
			} else if (c.absX  == 3260 && c.absY == 9329 && c.dungn == 3) {
				c.getPA().movePlayer(3261, 9331, 0);
			} else if (c.absX  == 3261 && c.absY == 9329 && c.dungn == 3) {
				c.getPA().movePlayer(3260, 9331, 0);
			} else if (c.absX  == 3246 && c.absY == 9333 && c.dungn == 6) {
					c.getPA().movePlayer(3244, 9333, 0);
			} else if (c.absX  == 3246 && c.absY == 9334 && c.dungn == 6) {
				c.getPA().movePlayer(3244, 9334, 0);
		        } else if (c.absX  == 3214 && c.absY == 9311 && c.dungn == 10) {
				c.getPA().movePlayer(3216, 9310, 0);
		        } else if (c.absX  == 3214 && c.absY == 9310 && c.dungn == 10) {
				c.getPA().movePlayer(3216, 9311, 0);
		        }
			break;

		case 6555:
			if (!Dung(c)) {
				c.sendMessage("You cannot use theses objects, teleport home and re-do the Dungeon.");
				return;
			} else if (c.absX  == 3233 && c.absY == 9323 && c.dungn == 0 ) {
				c.getPA().movePlayer(3233, 9325, 0);
			}
			break;

            		case 6501:
            			if (c.dungn == 14) {
            			c.getDH().sendDialogues(125, 1);
            			} else {
            				c.sendMessage("Go back and kill the NPCS.");
            			}
            		break;
			default:

			break;

		}	
	}

    public static boolean Dung(Client player) {
        return gameRoom.containsKey(player);
    }

}
