[code]public String GetNpcName(int NpcID) {
		for (int i = 0; i < NPCHandler.maxListedNPCs; i++) {
			if (NPCHandler.NpcList[i] != null) {
				if (NPCHandler.NpcList[i].npcId == NpcID) {
					return NPCHandler.NpcList[i].npcName;
				}
			}
		}
		return "asshole_rule";
	}

	public void talk2(String text, String text2, int npc) {
		getPA().sendFrame200(4901, 591);
		getPA().sendFrame126(GetNpcName(npc), 4902);
		getPA().sendFrame126("", 4903);
		getPA().sendFrame126(text, 4904);
		getPA().sendFrame126(text2, 4905);
		getPA().sendFrame126("", 4906);
		getPA().sendFrame75(npc, 4901);
		getPA().sendFrame164(4900);
	}[/code]

