[code]public void appendSlayerExperience(int i) {
		Client c = (Client)PlayerHandler.players[npcs[i].killedBy];
		if(c != null) {
			if (c.slayerTask == npcs[i].npcType){
				c.taskAmount--;
				c.getPA().addSkillXP(npcs[i].MaxHP * Config.SLAYER_EXPERIENCE, 18);
				if (c.taskAmount <= 0) {
					c.getPA().addSkillXP((npcs[i].MaxHP * 8) * Config.SLAYER_EXPERIENCE, 18);
					c.slayerTask = -1;
					int points = Misc.random(5) + 5;
					c.slayerPoints += points;
					if (!c.getPA().wearing2xRing())
						c.sendMessage("You have received " + points
							+ " slayer points for this. You now have "
							+ c.slayerPoints + " points");
					else {
						c.sendMessage("You have received " + points * 2
							+ " as it is doubled. You now have "
							+ c.slayerPoints + " points");
						c.slayerPoints += points;
					}
					c.sendMessage("You completed your slayer task. Please see a slayer master to get a new one.");
				}
			}
		}
	}[/code]

