			for (int i = 0; i < npcAmount; i++) {
				int npc = WAVES[c.waveId][i];
				int X = coordinates[i][0];
				int Y = coordinates[i][1];
				int H = c.heightLevel;
				int hp = HP[i][0];
				int max = MAX[i][0];
				int atk = ATK[i][0];
				int def = DEF[i][0];
				Server.npcHandler.spawnNpc(c, npc, X, Y, H, 0, hp, max, atk, def, true, false);
			}
			c.tzhaarToKill = npcAmount;
			c.tzhaarKilled = 0;
		}
