[size=2][b][anchor=Assistant]6.0 - DialogueHandler.java[/b][/size]

Now open DialogueHandler.java and add this import at the very top:

[/center]
[code=java]import server.Server;[/code]

Now add:


[code=java]
case 201:
                        sendPlayerChat2("I'm searching for an adventurous quest.","Do you have a quest for me?");
                        c.nextChat = 202;
                break;

case 202:
                        sendNpcChat4("Oh yes!", "I would like you to go to Sir Prysin. He's at Falador.", "He will tell you some things I don't want to say.", c.talkingNpc, "Fear Reaper");
			c.dialogueAction = 90;
                        c.nextChat = 203;
                break;

case 203:
                        sendPlayerChat2("Awesome!","I'll get going!");
                break;

case 204:
                        sendPlayerChat2("Hello! Fear Reaper sent me to you","for a quest!");
                        c.nextChat = 205;
                break;

case 205:
                        sendNpcChat4("So Fear Reaper send you to me? Okay!", "I would like you to go kill a dragon! It's at", "Seers' Village.", c.talkingNpc, "Sir Prysin");
                        c.nextChat = 206;
                break;

case 206:
                        sendPlayerChat2("What!? Why me me!? Why can't that","big guy do it, huh?");
                        c.nextChat = 207;
                break;

case 207:
                        sendNpcChat4("Yes you! Don't you argue with me!", c.talkingNpc, "Sir Prysin");
                        c.nextChat = 208;
                break;

case 208:
                        sendPlayerChat2("Fine!",);
                        c.dialogueAction = 208;
                break;

case 209:
                        sendPlayerChat2("I killed it!",);
                        c.nextChat = 210;
                break;

case 210:
                        sendNpcChat4("Great job! You have really proven yourself adventurer.", c.talkingNpc, "Sir Prysin");
                        c.dialogueAction = 210;
                        c.here += 1;
                        c.getPA().sendFrame126("@gre@Here We Go Again!", 29162);
                        c.nextChat = 0;
                break;

case 417:
                        c.getPA().addSkillXP(150000, 17);
                        c.getItems().addItem(989, 1);
                        c.getItems().addItem(11757, 1);
                        c.nextChat = 0;
                c.getPA().sendFrame126("You have completed Here We Go Again!",12144);
                c.getPA().sendFrame126("150,000 thieving experience" ,12150);
                c.getPA().sendFrame126("Varrock armour 1" ,12151);
