[code]
	private String GetForlvl2(int i, Client c) {
		if (c.playerLevel[c.playerCrafting] >= i)
			return "@gre@";

			return "@red@";
	}
	private String GetForlvl3(int i, Client c) {
		if (c.playerLevel[c.playerCrafting] >= i)
			return "@whi@";

			return "@bla@";
	}
