More leech formulas!
[code]private void applyLeeches(int index) {
		if (c.lastTime > 0) {
			c.lastTime--;
			return;
		}
		if (c.curseActive[10]) {
			if (Misc.random(19) == 0) {
				leechAttack(index);
				c.lastTime++;
				return;
			}
		}
		if (c.curseActive[11]) {
			if (Misc.random(19) == 0) {
				leechRanged(index);
				c.lastTime++;
				return;
			}
		}
		if (c.curseActive[12]) {
			if (Misc.random(19) == 0) {
				leechMagic(index);
				c.lastTime++;
				return;
			}
		}
		if (c.curseActive[13]) {
			if (Misc.random(19) == 0) {
				leechDefence(index);
				c.lastTime++;
				return;
			}
		}
		if (c.curseActive[14]) {
			if (Misc.random(19) == 0) {
				leechStrength(index);
				c.lastTime++;
				return;
			}
		}
		if (c.curseActive[16]) {
			if (Misc.random(19) == 0) {
				leechSpecial(index);
				c.lastTime++;
				return;
			}
		}
	}[/code]

