public class TheRestlessGhost {
	public static final int FATHER_AERECK = 456;
	public static final int HELP_BUTTON = 28174;
	public static final int FRAME_ID = 7342;
	private static final int FINISHED_STAGE = 20;
	public static final int FATHER_URHNEY = 458;
	private static final int GHOST_SPEAK_AMULET = 552;
	public static final int RESTLESS_GHOST = 457;
	public static final int WARLOCK = 459;
	public static final int RESTLESS_GHOST_SKULL = 553;
	public static final int RESTLESS_GHOST_COFFIN =2145;
public static void itemOnObject(Client player, int object, int objectX, int objectY, int item) {
	switch(object) {
	case RESTLESS_GHOST_COFFIN:
		if(item == RESTLESS_GHOST_SKULL && player.restlessGhostStage == 4) {
			theRestlessGhostFinish(player);
		}
		break;
	}
}

	public static void firstClickObject(Client player, int object, int objectX,
			int objectY) {
		// TODO Auto-generated method stub
		switch (object) {
		case 2148:
			if (objectX == 3103 && objectY == 9576) {
				player.getPA().teleTabTeleport(3104, 3162, 0, "teleTab");
			}
			break;
		case 2145:
			if (player.restlessGhostStage == FINISHED_STAGE)
				return;
			player.sendMessage("You search the coffin and find a human corpse!");
			Server.npcHandler.spawnNpc(player, RESTLESS_GHOST, 3249, 3194, 0,
					0, 300, 30, 70, 90, false, false);
			break;
		case 2147:
			if (objectX == 3104 && objectY == 3162) {
				player.getPA().teleTabTeleport(3104, 9576, 0, "teleTab");
				player.sendMessage("You go down the stairs");
			}
			break;
		case 350:
			if (objectX == 3120 && objectY == 9564
					&& player.restlessGhostStage == 3) {
				Server.npcHandler.spawnNpc(player, WARLOCK, 3119, 9566, 0, 0,
						20, 3, 3, 8, true, false);
				player.getItems().addItem(RESTLESS_GHOST_SKULL, 1);
				player.restlessGhostStage = 4;
			}
			break;
		}
	}

	public static void walkingPacket(Client player) {
		if (player.restlessGhostChoice1)
			player.restlessGhostChoice1 = false;
		if (player.restlessGhostChoice2)
			player.restlessGhostChoice2 = false;
		if (player.restlessGhostChoice3)
			player.restlessGhostChoice3 = false;
		if (player.restlessGhostChoice4)
			player.restlessGhostChoice4 = false;
		if (player.restlessGhostChoice5)
			player.restlessGhostChoice5 = false;
	}

	public static void firstClickNpc(Client player, int npc) {
		switch (npc) {
		case RESTLESS_GHOST:
			if (player.restlessGhostStage == 3
					&& player.playerEquipment[player.playerAmulet] == GHOST_SPEAK_AMULET) {
				player.getDH().sendNpcChat1("Please go find my skull.",
						RESTLESS_GHOST, "Restless Ghost");
				player.nextChat = 0;
			}
			if (player.restlessGhostStage == 2
					&& player.playerEquipment[player.playerAmulet] == GHOST_SPEAK_AMULET) {
				player.getDH().sendDialogues(2028, RESTLESS_GHOST);
			} else {
				player.getDH().sendNpcChat1("GAHHH!", RESTLESS_GHOST,
						"Restless Ghost");
				player.nextChat = 0;
			}
			break;
		case FATHER_AERECK:
			if (player.restlessGhostStage == Quest.NOT_STARTED_STAGE)
				player.getDH().sendDialogues(2000, FATHER_AERECK);
			if (player.restlessGhostStage == FINISHED_STAGE) {
				player.getDH().sendNpcChat1(
						"Welcome to the church of holy saradomin.",
						FATHER_AERECK, "Father Aereck");
				player.nextChat = 0;
			}
			break;
		case FATHER_URHNEY:
			if (player.restlessGhostStage == Quest.NOT_STARTED_STAGE) {
				player.getDH().sendNpcChat1("Go away im meditating.",
						FATHER_URHNEY, "Father Urhney");
				player.nextChat = 0;
			}
			if (player.restlessGhostStage == Quest.STARTED_STAGE)
				player.getDH().sendDialogues(2016, FATHER_URHNEY);
			if (player.restlessGhostStage == 2)
				player.getDH().sendDialogues(2026, FATHER_URHNEY);

		}
	}

	public static void actionButton(Client player, int button) {
		switch (button) {
		case HELP_BUTTON:
			showInformation(player);
			break;
		case 9157:
			if (player.restlessGhostChoice1) {
				player.restlessGhostChoice1 = false;
				player.getDH().sendDialogues(2003, FATHER_AERECK);
			}
				if (player.restlessGhostChoice2) {
					player.restlessGhostChoice2 = false;
					player.getDH().sendDialogues(2007, FATHER_AERECK);
				}
				if (player.restlessGhostChoice3) {
					player.restlessGhostChoice3 = false;
					player.getDH().sendDialogues(2018, FATHER_URHNEY);
				}
				if (player.restlessGhostChoice4) {
					player.restlessGhostChoice4 = false;
					player.getDH().sendDialogues(2021, FATHER_URHNEY);
				}
				if (player.restlessGhostChoice5) {
					player.restlessGhostChoice5 = false;
					player.getDH().sendDialogues(2033, RESTLESS_GHOST);
				}

			break;
		case 9158:
			if (player.restlessGhostChoice1) {
				player.restlessGhostChoice1 = false;
				player.getDH().sendDialogues(2002, FATHER_AERECK);
			}
			if (player.restlessGhostChoice2) {
				player.restlessGhostChoice2 = false;
				player.getDH().sendDialogues(2006, FATHER_AERECK);
			}
			if (player.restlessGhostChoice3) {
				player.restlessGhostChoice3 = false;
				player.getDH().sendNpcChat1("Go away im meditating.",
						FATHER_URHNEY, "Father Urhney");
				player.nextChat = 0;
			}
			if (player.restlessGhostChoice4) {
				player.restlessGhostChoice4 = false;
				player.getDH().sendNpcChat1("It's none of your business.",
						FATHER_URHNEY, "Father Urhney");
				player.nextChat = 0;
			}
			if (player.restlessGhostChoice5) {
				player.restlessGhostChoice5 = false;
				player.getDH().sendDialogues(2033, RESTLESS_GHOST);
			}
			break;
		}
	}

