	public static void refresQuestTab(Client player) {
		if (player.restlessGhostStage == Quest.NOT_STARTED_STAGE) {
			player.getPA().sendFrame126("@red@The Restless Ghost", 7342);
		}
		if (player.restlessGhostStage >= Quest.STARTED_STAGE
				&& player.restlessGhostStage < FINISHED_STAGE) {
			player.getPA().sendFrame126("@yel@The Restless Ghost", 7342);
		}
		if (player.restlessGhostStage == FINISHED_STAGE) {
			player.getPA().sendFrame126("@gre@The Restless Ghost", FRAME_ID);
		}
	}

	public static void theRestlessGhostFinish(Client c) {
		c.restlessGhostStage = FINISHED_STAGE;
		c.getPA().addSkillXP(500000, 5);
		c.getPA().showInterface(297);
		c.getPA().showInterface(12140);
		c.getPA().sendFrame126("You have completed: The Restless Ghost", 12144);
		c.getPA().sendFrame126("500,000 Prayer Experience", 12150);
		c.getPA().sendFrame126("Ghost Speak amulet", 12151);
		c.getPA().sendFrame126("", 12152);
		c.getPA().sendFrame126("", 12153);
		c.getPA().sendFrame126("", 12154);
		c.getPA().sendFrame126("", 12155);
		// c.getPA().sendFrame126("@gre@Cook's Assistant", 7332);
	}

	public static void showInformation(Client c) {
		for (int i = 8144; i < 8195; i++) {
			c.getPA().sendFrame126("", i);
		}
		c.getPA().sendFrame126("@red@The Restless Ghost", 8144);
		c.getPA().sendFrame126("", 8145);
		if (c.restlessGhostStage == 0) {
			c.getPA().sendFrame126("@red@The Restless Ghost", 8144);
			c.getPA()
					.sendFrame126(
							"@blu@I can start this quest by speaking to@red@Father Aereck @blu@in the",
							8147);
			c.getPA()
					.sendFrame126(
							"@red@church @blu@next to @red@Lumbridge Castle.",
							8148);
			c.getPA().sendFrame126("", 8149);
			c.getPA()
					.sendFrame126(
