							8150);
		} else if (c.restlessGhostStage == 1) {
			c.getPA().sendFrame126("@red@The Restless Ghost", 8144);
			c.getPA().sendFrame126(
					"@blu@I have talked to@blu@ @red@Father Aereck@red@", 8147);
			c.getPA()
					.sendFrame126(
							"@blu@He sent me to see Father Urhney in@blu@ @red@Lumbridge Sawmp.",
							8148);
			c.getPA().sendFrame126("", 8149);
			c.getPA().sendFrame126("", 8150);
		} else if (c.restlessGhostStage == 2) {
			c.getPA().sendFrame126("@red@The Restless Ghost", 8144);
			c.getPA().sendFrame126("@blu@I have talked to Father Urhney", 8147);
			c.getPA().sendFrame126(
