			c.getPA().sendFrame126("", 8150);
		} else if (c.restlessGhostStage == 3) {
			c.getPA().sendFrame126("@red@The Restless Ghost", 8144);
			c.getPA().sendFrame126("@blu@I have talked the ghost", 8147);
			c.getPA().sendFrame126(
					"@blu@His corpse is missing his skull.", 8148);
			c.getPA()
					.sendFrame126(
							"@blu@ I should try to recover it at wizards tower.",
							8149);
			c.getPA().sendFrame126("", 8150);
		}
	 else if (c.restlessGhostStage == 4) {
		c.getPA().sendFrame126("@red@The Restless Ghost", 8144);
		c.getPA().sendFrame126("@blu@I have recovered the ghost's skull", 8147);
		c.getPA().sendFrame126(
				"@blu@I should head back to the graveyard to use it on his coffin.", 8148);
		c.getPA()
				.sendFrame126(
						"@blu@ ",
						8149);
		c.getPA().sendFrame126("", 8150);
	}
	 else if (c.restlessGhostStage == FINISHED_STAGE) {
			c.getPA().sendFrame126("@red@The Restless Ghost", 8144);
			c.getPA().sendFrame126("@blu@I have completed this quest!", 8147);
			c.getPA().sendFrame126(
					"@blu@I have received 500,000 prayer experience, and a ghost speak amulet.", 8148);
			c.getPA()
					.sendFrame126(
							"@blu@ ",
