	public int headIconPrayer = 0;
	public int attackPrayer = 0;
	public int defencePrayer = 0;
	public int strengthPrayer = 0;

	public void updatePrayer(int amount) {

		if (client.isDead())
			return;
		client.getActionAssistant()
				.decreaseStat(PlayerConstants.PRAYER, amount);
		client.getActionSender().sendQuest(
				"" + client.playerLevel[PlayerConstants.PRAYER] + "", 4012);
	}

	public void setPrayerIcon(int i) {
		client.headIcon = i;
		client.updateRequired = true;
		client.appearanceUpdateRequired = true;
	}

	public void resetAllPrayers() {
		for (int i = 0; i < clicked.length; i++) {
			clicked[i] = false;
			client.getActionSender().sendFrame36(GLOW_IDS[i], 0);
		}
		headIconPrayer = 0;
		attackPrayer = 0;
		defencePrayer = 0;
		strengthPrayer = 0;
		setPrayerIcon(-1);
	}


	public int getActivePrayers() {
		int active = 0;
		for (boolean c : clicked) {
			if (c == true)
				active++;
		}
		if (Jad.inArea(client))
			active *= 4;

		return active;
	}

	public void prayerEvent() {

		if (client.playerLevel[PlayerConstants.PRAYER] <= 0 && getActivePrayers() > 0) {
			client.getActionSender().sendMessage("You do not have enough prayer points.");
			resetAllPrayers();
			return;
		}
		updatePrayer(getActivePrayers());
		if (client.playerLevel[PlayerConstants.PRAYER] <= 0 && getActivePrayers() > 0) {
			client.getActionSender().sendMessage("You do not have enough prayer points.");
			resetAllPrayers();
			return;
		}
	}

	public void activatePrayer(int prayerId) {
		if (client.getLevelForXP(client.playerXP[PlayerConstants.PRAYER]) < PRAYER_CONFIGS[prayerId][1]) {
			client.getActionSender().sendFrame36(GLOW_IDS[prayerId], 0);
			if (client.getLevelForXP(client.playerXP[PlayerConstants.DEFENCE]) < PRAYER_CONFIGS[prayerId][3]) {
				client.getActionSender().sendQuest("",369);
				client.getActionSender().sendQuest("You need a @blu@Prayer level of "+ PRAYER_CONFIGS[prayerId][1] +"@bla@ to use @blu@"+ PRAYER_NAMES[prayerId] +".",370);
				client.getActionSender().sendQuest("You also need a @blu@Defence Level of " + PRAYER_CONFIGS[prayerId][3] + ".",371);
				client.getActionSender().sendQuest("",372);
				client.getActionSender().sendQuest("Click here to continue", 373);
				client.getActionSender().sendFrame164(368);
