public void months2() {

        int month = 2;
        int year = 2009;
        int numDays = 0;

        switch (month) {
            case 1:
            case 3:
            case 5:
                numDays = 31;
                break;
            case 4:
                numDays = 30;
                break;
            case 2:
                if ( ((year % 4 == 0) && !(year % 100 == 0))
                     || (year % 400 == 0) )
                    numDays = 30;
                else
                    numDays = 31;
                break;
            default:
                System.out.println("Invalid month.");
                break;
        }
        System.out.println("Number of Days = " + numDays);
    }[/code]
