[code=java]public abstract class AbstractServer {

	/**
	 * Sets up the utilities for the networking.
	 */
	public abstract void initializeNetwork();

	/**
	 * Binds the server to a specified port.
	 * 
	 * @param port The port.
	 */
	public abstract void bind(int port);

	/**
	 * Starts the main tick of the server.
	 * 
	 * @param cycleTime The cycle time of the server in milliseconds.
	 */
	public abstract void runMainTick(int cycleTime);

	/**
	 * Quits the game server.
	 */
	public abstract void quit();

	/**
	 * Retrieves the task scheduler.
	 * 
	 * @return The task scheduler.
	 */
	public abstract TaskScheduler getTaskScheduler();

}[/code]

