[code=java]/**
	 * Initializes the players repository.
	 */
	public void Initialize() {
		players.clear();
		System.out.println("Cleared any corrupt online players.");
	}

	/**
	 * Unloads the players repository.
	 */
	public void Unload() {
		for (Player player : players) {
			try {
				FileAccess.savePlayer(player);
				System.out.println("Saved player accounts.");
			} catch (IOException e) {
				System.out.println("Failed to save player accounts.");
				e.printStackTrace();
			}
		}
		players.clear();
		System.out.println("Cleared online players.");
		System.out.println("Finished unloading the player repository.");
	}[/code]

