import java.awt.Toolkit;
import java.awt.datatransfer.Clipboard;
import java.awt.datatransfer.StringSelection;
import java.util.Scanner;

/**
 * A very simple class dedicated to the rune-server
 * posts. Please do not use this in the shoutbox as it
 * is not aloud.
 * @author Zack/Optimum
 *
 */
public class RainbowText {

	/**
	 * This will keep a track of the next colour to add
	 */
	private static byte colorIndex = 0;

	/**
	 * A list of the all the possible colours
	 */
	private final static String[] COLOURS = { //Rainbow Colour scheme
		"#FF2B21","#FF7C21","#FFDD56",
		"#B4FF00","#00FF48","#00C2EF", "#0050EF",
		"#6F09EF", "#E909FF"
	};

	/*
	private final static String[] COLOURS = { //monochromatic red colour scheme
		"#ffffff","#ffe5e5","#ffcccc",
		"#ffb2b2","#ff9999","#ff7f7f",
		"#ff6666", "#ff4c4c", "#ff3232",
		"#ff1919", "#ff4c4c", "#ff6666",
		"#ff7f7f", "#ff9999", "#ffb2b2",
		"#ffcccc", "#ffe5e5",
	};*/

	/**
	 * This will find the next colour to add to
	 * the character
	 * @return 
	 * 			- the colour string to add
	 */
	private static String applyColour(){
		colorIndex++;
		if(colorIndex >= COLOURS.length)
			colorIndex = 0;
		return COLOURS[colorIndex];
	}

	/**
	 * This will check the console for inputs and
	 * add the next colour in line to the characters
	 * then it will copy the text to your clipboard
	 * @param args
	 */
	@SuppressWarnings("resource")
	public static void main(String[] args){
		String msg = "";
		Scanner s = new Scanner(System.in);
		while(s.hasNext()){
			System.out.print("[B][SIZE=32pt][FONT=Courier New]");
			msg += "[B][SIZE=4][FONT=Courier New]";
			String letter = s.next();
				System.out.print(" ");
				msg += " ";
			for (int i = 0; i < letter.length(); i++) {
				System.out.print("" + letter.substring(i, i + 1) + "");
				msg += ("" + letter.substring(i, i + 1) + "");
			}
			System.out.print("[/FONT][/SIZE][/B]");
			msg += "[/FONT][/SIZE][/B]";
			StringSelection message = new StringSelection(msg);
			Clipboard c = Toolkit.getDefaultToolkit().getSystemClipboard();
			c.setContents(message, null);
		}
	}
}
